<template>
  <label class="seed-checkbox">
    <input
      type="checkbox"
      class="seed-checkbox__input"
      :checked="localChecked"
      :disabled="disabled"
      @change="handleChange"
    />
    <div class="seed-checkbox__mark"></div>
  </label>
</template>

<script>
import '../style/index.less';
export default {
  name: 'checkbox',
  props: {
    checked: {
      type: Boolean,
      default: false,
    },
    disabled: {
      type: Boolean,
      default: false,
    },
    value: {
      type: Boolean,
      default: false,
    },
  },
  data() {
    return {
      localChecked: false,
    };
  },
  created() {
    this.localChecked = this.value || this.checked;
  },
  watch: {
    value() {
      this.localChecked = this.value;
      this.emitEvent();
    },
    checked() {
      this.localChecked = this.checked;
      this.emitEvent();
    },
  },
  methods: {
    handleChange() {
      if (this.disabled) return;
      this.localChecked = !this.localChecked;
      this.emitEvent();
    },
    emitEvent() {
      this.$emit('input', this.localChecked);
      this.$emit('change', this.localChecked);
    },
  },
};
</script>
