<template>
  <span
    :class="[
      'seed-icon-' + name,
      type ? 'seed-icon-' + type : '',
      size ? 'seed-icon-' + size : '',
      selected ? 'seed-icon-selected' : '',
    ]"
  ></span>
</template>

<script>
import '../style/index.less';
export default {
  name: 'icon',
  props: {
    name: {
      type: String,
      default: '',
    },
    type: {
      type: String,
      default: '',
    },
    size: {
      type: String,
      validator: function(value) {
        return ['lg', 'sm'].indexOf(value) > -1;
      },
    },
    selected: {
      type: Boolean,
      default: false,
    },
  },
};
</script>
