<template>
  <ul
    :class="{
      'seed-page-control': true,
      'seed-page-control--sm': size === 'sm',
    }"
  >
    <li
      v-for="index in count"
      :key="index"
      :class="{
        'seed-page-control__item': true,
        'seed-page-control__item--sm': size === 'sm',
        'seed-page-control__item--selected': index - 1 == selectedIndex,
      }"
      :style="{
        backgroundColor: index - 1 == selectedIndex ? selectedColor : color,
      }"
    ></li>
  </ul>
</template>

<script>
import '../style/index.less';
export default {
  name: 'page-control',
  props: {
    size: {
      type: String,
      validator: function(value) {
        return ['', 'lg', 'sm'].indexOf(value) > -1;
      },
    },
    selectedColor: String,
    color: String,
    count: Number,
    selectedIndex: Number,
  },
};
</script>
