<template>
  <div :class="classes.placeholder"></div>
</template>

<script>
import '../style/index.less';
import { prefixCls } from '@seed/ui-base';

export default {
  name: 'placeholder',
  props: {
    type: {
      type: String,
      default: 'retrieval',
      validator(value) {
        return [
          'retrieval',
          'error',
          'address',
          'collection',
          'comment',
          'discount',
          'good-friend',
          'network',
          'news',
          'order',
          'search',
        ].includes(value);
      },
    },
  },
  computed: {
    classes() {
      return {
        placeholder: [
          prefixCls('placeholder'),
          prefixCls('placeholder', this.type),
        ],
      };
    },
  },
};
</script>
