<!-- @since 2018-11-30 13:55 -->
<!-- @author vivaxy -->
<template>
  <div
    :class="classes.separator"
    :style="
      'background-color: ' +
        color +
        '; marginLeft: ' +
        marginHorizontal +
        '; marginRight: ' +
        marginHorizontal
    "
  ></div>
</template>

<script>
import '../style/index.less';
import { prefixCls } from '@seed/ui-base';

export default {
  name: 'separator',
  props: {
    size: {
      type: String,
      default: 'sm',
      validator(value) {
        return ['sm', 'lg', 'md'].includes(value);
      },
    },
    color: {
      type: String,
      default: null,
    },
    marginHorizontal: {
      type: String,
      default: '0',
    },
  },
  computed: {
    classes() {
      return {
        separator: [
          prefixCls('separator'),
          prefixCls('separator', 'line', this.size),
        ],
      };
    },
  },
};
</script>
