export default function numberCeil(count, text) {
  if (!count) {
    return '';
  }
  if (typeof count == 'string') {
    count = parseInt(count, 10);
  }
  let baseNum = 10000;
  if (count < baseNum) {
    return count + (text || '');
  }
  let result = count / baseNum;
  let integer = Math.floor(result);
  let decimals = Math.ceil((result - integer) * 10);
  if (decimals == 10) {
    ++integer;
    decimals = 0;
  }
  return `${integer}.${decimals}ä¸‡`;
}
