import React from 'react';
import PropTypes from 'prop-types';
import ImageCover from '../../image-cover/react/index';
import '../style/index.less';
import cls from 'classnames';
import { prefixCls } from '@seed/ui-base';

export default function Avatar(props) {
  const { styles, width, url, loading, imageCoverData } = props;
  const wrapStyle = {
    width: width / 2 + 'px',
    height: width / 2 + 'px',
    backgroundImage: url ? `url(${url})` : null,
  };
  return (
    <div
      style={Object.assign({}, wrapStyle, styles)}
      className={cls(
        prefixCls('avatar-container'),
        loading ? prefixCls('avatar-loading') : '',
      )}
    >
      {imageCoverData && <ImageCover {...imageCoverData} />}
    </div>
  );
}

Avatar.propTypes = {
  // 增加icon
  // 增加overlay
  url: PropTypes.string,
  styles: PropTypes.object,
  width: PropTypes.number,
  defaultImg: PropTypes.bool,
  imageCoverData: PropTypes.object,
};
