import React from 'react';
import PropTypes from 'prop-types';
import '../style/index.less';
import cls from 'classnames';
import { prefixCls } from '@seed/ui-base';

export default class Loading extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    const { show, loadingText, loadingStyles, vCenter } = this.props;
    if (show === false) {
      return null;
    }
    return (
      <div
        style={loadingStyles}
        className={cls(prefixCls('loading-container'), {
          [prefixCls('v-center')]: vCenter,
        })}
      >
        <div className={prefixCls('load-wrap')}>
          <div className={prefixCls('loading-img')} />
          <span>{loadingText ? loadingText : ''}</span>
        </div>
      </div>
    );
  }
}
Loading.propTypes = {
  loading: PropTypes.bool,
  // 增加icon
  // 增加overlay
  vCenter: PropTypes.bool,
  loadingStyles: PropTypes.object,
  loadingText: PropTypes.string,
};
