<template>
  <div :style="loadingStyles" :class="containerClasses">
    <div :class="wrapClasses">
      <div :class="imgClasses" />
      <span v-if="loadingText">{{ loadingText }}</span>
    </div>
  </div>
</template>

<script>
import '../style/index.less';
import { prefixCls, priceZeroClear } from '@seed/ui-base';

export default {
  name: 'loading',
  // data:function(){
  //     return {

  //     }
  // },
  props: {
    loadingText: {
      type: String,
      default: '',
    },
    loading: {
      type: Boolean,
      default: false,
    },
    vCenter: {
      type: Boolean,
      default: false,
    },
    loadingStyles: {
      type: Object,
    },
  },
  computed: {
    containerClasses() {
      return [
        prefixCls('loading-container'),
        { [prefixCls('v-center')]: this.vCenter },
      ];
    },
    wrapClasses() {
      return [prefixCls('load-wrap')];
    },
    imgClasses() {
      return [prefixCls('loading-img')];
    },
  },
  methods: {
    prefixCls(...arg) {
      return prefixCls(...arg);
    },
    priceZeroClear(...arg) {
      return priceZeroClear(...arg);
    },
  },
};
</script>
