import React, { Component } from 'react';

import PropTypes from 'prop-types';
import cls from 'classnames';
import { prefixCls } from '@seed/ui-base';
import '../style/mtstar.less';
export default class MTStar extends Component {
  render() {
    const { star } = this.props;
    let starInit = Array.from({ length: 5 });
    const starValueRound = Math.round(star / 10);
    const starValueCeil = Math.ceil(star / 10);
    const starList = starInit.map((item, index) => {
      return {
        value: index + 1 <= starValueRound ? index + 1 : 0,
        isHalf: star % 10 > 0 && index + 1 === starValueCeil,
      };
    });
    return (
      <div className={prefixCls('star-mt-wrap')}>
        {starList.map((ele, index) => {
          return (
            <div
              className={cls(
                prefixCls('star-mt'),
                prefixCls(`star-mt-${ele.value}`),
              )}
              key={index}
            >
              {ele.isHalf && <div className={prefixCls('star-mt-half')} />}
            </div>
          );
        })}
      </div>
    );
  }
}
