export default function toFixed(number, length) {
  if (number >= Math.pow(10, length) || number < 0) {
    throw new Error(`Error number \`${number}\` to fixed length ${length}.`);
  }
  const numberString = String(number);
  return (
    Array.from({ length: length - numberString.length }, () => {
      return 0;
    }) + numberString
  );
}
