/**
 * vue 文件入口
 */
import Button from './button/vue/index.vue';
import Currency from './currency/vue/index.vue';
import Loading from './loading/vue/index.vue';
import DateTime from './datetime/vue/index.vue';
import Badge from './badge/vue/index.vue';
import PageControl from './page-control/vue/index.vue';
import Checktag from './checktag/vue/index.vue';
import Checkbox from './checkbox/vue/index.vue';
import Icon from './icon/vue/index.vue';
import Separator from './separator/vue/index.vue';
import Tag from './tag/vue/index.vue';
import Slider from './slider/vue/index.vue';
import Switch from './switch/vue/index.vue';
import NoviceGuidance from './novice-guidance/vue/index.vue';
import Star from './star/vue/index.vue';
import MTStar from './mtstar/vue/index.vue';
import Placeholder from './placeholder/vue/index.vue';
import Avatar from './avatar/vue/index.vue';
import ImageCover from './image-cover/vue/index.vue';
import AnimatedInteraction from './animated-interaction/vue/index.vue';

const globalName = 'seed-';

let install = function(Vue, options = {}) {
  if (install.installed) return;
  const components = [
    Button,
    Currency,
    Loading,
    DateTime,
    Badge,
    PageControl,
    Checktag,
    Checkbox,
    Icon,
    Separator,
    Tag,
    Avatar,
    ImageCover,
    Slider,
    Switch,
    NoviceGuidance,
    Placeholder,
    MTStar,
    AnimatedInteraction,
  ];
  components.forEach((item, index) => {
    Vue.component(globalName + item.name, item);
  });
  Vue.component('seed-star', Star);
};

export default install;
